<template>

    <div id="lf" style="height: 100%;"></div>
</template>

<script>
import {Menu, BpmnElement, SelectionSelect, Control, MiniMap, Snapshot} from '@logicflow/extension'
import {lfJson2Xml} from '@logicflow/extension'
import {LogicFlow, PolygonNode, PolygonNodeModel, RectNode, RectNodeModel} from '@logicflow/core'
import {SuanziPanel} from './SuanziPanel.js'
import {FlowCharPanel} from './FlowCharPanel.js'

class TriangleModel extends PolygonNodeModel { // 三角形
    setAttributes() {
        this.points = [
            [50, 0],
            [100, 80],
            [0, 80]
        ]
    }
}


class SuanziModel extends RectNodeModel {
    setAttributes() {
        const size = this.properties.scale || 1;
        this.width = 100 * size
        this.height = 80 * size
    }
}


export default {
    name: 'FlowDemo',
    data() {
        return {
            lf: null,
            initHeight: '',
            initData: null,
        }
    },
    mounted() {
        this.initHeight = window.innerHeight
        this.init()
        window.onresize = () => {
            return (() => {
                this.initHeight = window.innerHeight
                this.lf.render(this.lf.getGraphData())
                const position =lf.getPointByClient(document.documentElement.clientWidth-150,document.documentElement.clientHeight-230)
                lf.extension.miniMap.show(position.domOverlayPosition.x, position.domOverlayPosition.y)
            })
        }
    },
    methods: {
        init() {
            const lf = new LogicFlow({
                container: document.querySelector("#lf"),
                height: this.initHeight,
                plugins: [Menu, BpmnElement, FlowCharPanel, SelectionSelect, Control, MiniMap, Snapshot, SuanziPanel],
                background: {
                    color: '#2b364a' // 网格背景颜色
                },
                keyboard: {
                    enabled: true // 支持快捷键操作
                },
                grid: { // 开启网格
                    type: 'mesh', // 网格类型为线状
                    size: 20,
                    visible: true // 是否可见
                }
            })


            lf.extension.suanziPanel.setPatternItems([
                {
                    type: "suanzi",
                    text: "算子一",
                    label: "算子一",
                    icon:
                        "data:image/png;base64,"
                },
                {
                    type: 'suanzi',
                    text: '算子二',
                    label: '算子二',
                    icon: 'data:image/png;base64,'
                },
            ])

            // 设置节点面板
            lf.extension.flowCharPanel.setPatternItems([
                {
                    label: "选区",
                    icon:
                        "data:image/png;base64,",
                    callback: () => {
                        lf.openSelectionSelect();
                        lf.once("selection:selected", () => {
                            lf.closeSelectionSelect();
                        });
                    }
                },
                // {
                //     type: "bpmn:startEvent",
                //     text: "开始",
                //     label: "开始节点",
                //     icon:
                //         "data:image/png;base64,"
                // },
                {
                    type: "bpmn:userTask",
                    label: "用户任务",
                    icon:
                        "data:image/png;base64,",
                    className: "important-node"
                },
                {
                    type: "bpmn:serviceTask",
                    label: "系统任务",
                    icon:
                        "data:image/png;base64,",
                    className: "import_icon"
                },
                {
                    type: "bpmn:exclusiveGateway",
                    label: "条件判断",
                    icon:
                        "data:image/png;base64,"
                },
                {
                    type: 'triangle',
                    text: '三角形',
                    label: '三角形',
                    icon: 'data:image/png;base64,'
                },
                {
                    type: "bpmn:endEvent",
                    text: "结束",
                    label: "结束节点",
                    icon:
                        "data:image/png;base64,"
                },
            ])
            lf.batchRegister([{ // 三角形节点
                type: 'triangle',
                view: PolygonNode,
                model: TriangleModel
            },
                {
                    type: 'suanzi',
                    view: RectNode,
                    model: SuanziModel
                }
            ])

            lf.extension.control.addItem({
                text: "下载快照",
                onClick: () => {
                    this.downloadImage()
                }
            })
            lf.extension.control.addItem({
                text: "下载XML",
                onClick: () => {
                    this.downloadXML()
                }
            })

            const initData = {

            }

            lf.render(initData)
            const position =lf.getPointByClient(document.documentElement.clientWidth-150,document.documentElement.clientHeight-230)
            lf.extension.miniMap.show(position.domOverlayPosition.x, position.domOverlayPosition.y)
            this.lf = lf
            this.initData = initData

        },
        download(filename, text) {
            console.log(filename, text)
            var element = document.createElement("a")
            element.setAttribute(
                "href",
                "data:text/plain;charset=utf-8," + encodeURIComponent(text)
            )
            element.setAttribute("download", filename)

            element.style.display = "none"
            document.body.appendChild(element)

            element.click()

            document.body.removeChild(element)
        },
        downloadImage() {
            this.lf.getSnapshot()
        },
        downloadXML() {
            this.download('flow.xml', lfJson2Xml(this.lf.getGraphData()))
        }
    },

}
</script>

<style>

.custom-minimap {
    background-image: url(sys/logo.png);
}


